import { contains } from "./contains";

let flowerInBascet = {
    flower: {},
    arrayFlowerForBascet: []
}

export const reducer = (state = flowerInBascet, action) => {   
    if (action.type == 'setFlower') {
        return {...state, flowerflower: action.payload}
    }
    if (action.type == 'getFlower') {
        return {...state};
    }
    if (action.type == 'isBascet') {
        return {...state, arrayFlowerForBascet: [state.arrayFlowerForBascet, action.payload]}
    }
    
}
