import { contains } from "./contains";

let flowerInBascet = {
    flower: {},
    arrayFlowerForBascet: [],

    inBascet: function() {
        if (this.arrayFlowerForBascet.includes(this.flower)) {
            return true;
        }
        else return false;
    }
}

export const reducer = (state = flowerInBascet, action) => {   
    if (action.type == 'setFlower') {
        return {...state, flower: action.payload}
    }
    if (action.type == 'getFlower') {
        return {...state};
    }
    if (action.type == 'inBascet') {
        if (!state.inBascet()) {
            return {...state, flower: action.payload, ...state.arrayFlowerForBascet.push(action.payload)}
        }
        else {
            return {...state, flower: action.payload}
        }
    }
    return {...state};
    
}
