import { useDispatch, useSelector } from "react-redux";
import {Route, Routes, Link} from "react-router-dom";
import { addFlowerInBascet, inBascet } from "../store/actions/action_1";
import { useEffect, useState } from "react";
import { contains } from "../store/reducers/contains";
import '../styles/relatedProduct.css'

function RelatedProduct() {
    const [stringInBascet, setStringInBascet] = useState("Добавить в корзину")
 
    const product = useSelector(state => state.product)
    const arrayFlowerForBascet = useSelector(state => state.arrayFlowerForBascet)
    const dispatch = useDispatch()
    
    const onClickButtonBuy = () => {
        if (!contains(arrayFlowerForBascet, flower)) {
            dispatch(addFlowerInBascet(flower));
            setStringInBascet("В корзине");
        }
    }

    useEffect(() => {
        if (!contains(arrayFlowerForBascet, flower)) {
            setStringInBascet("Добавить в корзину")
        }
        else {
            setStringInBascet("В корзине")
        }
    })


    return(
        <div className = "related-product">
            <div className = "related-product-upper">
                <div className = "related-product-upper-left">
                    <img src = {flower['URL']} className="related-product-upper-left"></img>
                </div>
                <div className = "related-product-upper-right">
                    <div className="related-product-upper-right-upper">
                        <p className="related-text-product">{flower['Наименование']}</p>
                        <p className="related-text-product">Количество букетов: {flower['Количество']}</p>
                        <p className="related-text-product-cost">Цена: {flower['Цена']}</p>
                    </div>
                    <div className="related-product-upper-right-lower">
                    <button className="related-button-buy-product" onClick={onClickButtonBuy}>
                            {stringInBascet}
                        </button>
                    </div>
                </div>
            </div>
            <div className = "product-lower">
            </div>
        </div>
    );
}

export default RelatedProduct;
