let flowerInBascet = {
    flower: {},
    arrayFlowerForBascet: []
}
const contains = (arr, elem) => {
    return arr.includes(elem) != 1;
}

export const reducer = (state = flowerInBascet, action) => {   
    if (action.type == 'setFlower') {
        return state.flower = action.payload;
    }
    if (action.type == 'getFlower') {
        return state.flower;
    }
    if (action.type == 'isBascet') {
        if (!contains(state.arrayFlowerForBascet, state.flower)) {
            state.arrayFlowerForBascet.push(state.flower)
        }
        return state.flower;
    }
    
}
