import React, { useState } from "react"
import '../styles/dropdown.css'

export default function Dropdown() {
    const [isOpen, setIsOpen] = useState(false);

    return(
        <div className="dropdown">
            <button className = 'button-dropdown-one' onClick={() => !isOpen ? setIsOpen(true) : setIsOpen(false)}>По цене:</button>
            <div> {
                isOpen && <ul>
                    <li className="list">По возрастанию</li>
                    <li className="list">По убыванию</li>
                </ul> 
                }
            </div>
        </div>
    );
}
